INSERT INTO ricetta (titolo, ingredienti, url_foto, tempo_preparazione, porzioni, testo_ricetta,data_creazione) VALUES ('Pasta al Pomodoro', '200g di pasta, 400g di pomodori, olio d\'oliva, basilico, sale, pepe', 'https://example.com/pasta.jpg', 30, 2, '1. Cuocere la pasta...\n2. Preparare il sugo di pomodoro...\n3. Servire con basilico e olio d\'oliva.','2023-09-29');
INSERT INTO ricetta (titolo, ingredienti, url_foto, tempo_preparazione, porzioni, testo_ricetta, data_creazione) VALUES ('Risotto ai Funghi', '200g di riso, 200g di funghi, brodo, cipolla, burro, vino bianco, parmigiano', 'https://example.com/risotto.jpg', 40, 2, '1. Rosolare la cipolla...\n2. Aggiungere i funghi...\n3. Aggiungere il vino bianco e il riso...\n4. Servire con parmigiano.', '2023-09-28');
INSERT INTO ricetta (titolo, ingredienti, url_foto, tempo_preparazione, porzioni, testo_ricetta, data_creazione) VALUES ('Spaghetti alla Carbonara', '200g di spaghetti, uova, pancetta, pecorino romano, pepe', 'https://example.com/carbonara.jpg', 25, 2, '1. Cuocere gli spaghetti...\n2. Rosolare la pancetta...\n3. Mescolare le uova con il pecorino...\n4. Mescolare tutto insieme con pepe.', '2023-09-26');
INSERT INTO ricetta (titolo, ingredienti, url_foto, tempo_preparazione, porzioni, testo_ricetta, data_creazione) VALUES ('Tiramisù', 'Savoiardi, caffè, uova, zucchero, mascarpone, cacao in polvere', 'https://example.com/tiramisu.jpg', 40, 6, '1. Preparare il caffè...\n2. Montare le uova con lo zucchero...\n3. Mescolare il mascarpone...\n4. Alternare strati di crema e savoiardi...\n5. Spolverare con cacao in polvere.', '2023-09-27');
